CREATE OR REPLACE FUNCTION ctb_obter_se_centro_sup3 ( cd_classif_atual_p text, cd_classif_sup_p text) RETURNS varchar AS $body$
DECLARE


ds_classif_atual_w		varchar(255);
ds_classif_sup_w		varchar(255);
qt_comma_classif_w		bigint;
qt_comma_classif_sup_w		bigint;
qt_campo_w			bigint;
ie_pertence_w			varchar(1);


BEGIN
ie_pertence_w		:= 'N';
ds_classif_sup_w	:= cd_classif_sup_p;
ds_classif_atual_w	:= cd_classif_atual_p;

begin
qt_comma_classif_sup_w	:= length(ds_classif_sup_w) - length(replace(ds_classif_sup_w, '.', ''));
qt_comma_classif_w	:= length(ds_classif_atual_w) - length(replace(ds_classif_atual_w, '.', ''));
qt_campo_w		:= 0;
if (qt_comma_classif_sup_w <= qt_comma_classif_w) and (ds_classif_sup_w <> ds_classif_atual_w) then
	begin
	while(qt_campo_w <= qt_comma_classif_sup_w) loop
		begin
		qt_campo_w := qt_campo_w + 1;
		if (regexp_substr(ds_classif_sup_w,'[^.]+',1,qt_campo_w) = regexp_substr(ds_classif_atual_w,'[^.]+',1,qt_campo_w)) then
			begin
			ie_pertence_w := 'S';
			end;
		else
			begin
			ie_pertence_w := 'N';
			exit;
			end;
		end if;
		end;
	end loop;
	end;
end if;
exception
when others then
	ie_pertence_w := 'N';
end;

return ie_pertence_w;

end;
