CREATE OR REPLACE FUNCTION ctb_obter_se_centro_usu_estab ( cd_centro_custo_p bigint, cd_empresa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_atualizacao_w				varchar(01)	:= 'S';
ie_pertence_w				varchar(01)	:= 'N';
cd_classificacao_w				varchar(40);
cd_classif_centro_w			varchar(40);
cd_estabelecimento_w			smallint;
cd_estab_centro_custo_w			smallint;
cd_perfil_w				integer;

C01 CURSOR FOR
SELECT	ie_atualizacao,
	cd_classif_centro
from	ctb_lib_orcamento
where	((nm_usuario_lib	= nm_usuario_p) or (coalesce(nm_usuario_lib::text, '') = ''))
and	cd_empresa		= cd_empresa_p
and	((cd_perfil 		= cd_perfil_w) or (coalesce(cd_perfil::text, '') = ''))
and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
order by cd_classif_centro;


BEGIN

cd_perfil_w		:=	wheb_usuario_pck.get_cd_perfil;

begin
	cd_estabelecimento_w := coalesce(cd_estabelecimento_p,coalesce(wheb_usuario_pck.get_cd_estabelecimento,0));
exception when others then
	cd_estabelecimento_w := 0;
	--'Erro ao obter o Estabelecimento'
	CALL wheb_mensagem_pck.exibir_mensagem_abort(242213);
end;

select	cd_classificacao,
	cd_estabelecimento
into STRICT	cd_classificacao_w,
	cd_estab_centro_custo_w
from	centro_custo
where	cd_centro_custo	= cd_centro_custo_p;

if (cd_estabelecimento_w = 0) then
	cd_estabelecimento_w := cd_estab_centro_custo_w;
end if;

open c01;
loop
fetch c01 into
	ie_atualizacao_w,
	cd_classif_centro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (substr(cd_classificacao_w,1, length(cd_classif_centro_w)) = cd_classif_centro_w) and (cd_classificacao_w IS NOT NULL AND cd_classificacao_w::text <> '') then
		ie_pertence_w	:= ie_atualizacao_w;
	end if;
end loop;
close c01;
return ie_pertence_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
