CREATE OR REPLACE FUNCTION ctb_obter_se_concilia_movto (cd_tipo_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_conciliar_w			tipo_lote_contabil.ie_conciliar%type;


BEGIN
	begin
	select	coalesce(max(ie_conciliar),'N')
	into STRICT	ie_conciliar_w
	from	tipo_lote_contabil
	where	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
	and	ie_situacao		= 'A';

	exception
	when others then
		ie_conciliar_w := 'N';
	end;

return	ie_conciliar_w;

end;
$body$
