CREATE OR REPLACE FUNCTION ctb_obter_se_conta_contida ( cd_conta_contabil_p text, cd_conta_parametro_p text) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_ww		varchar(2000);
ie_gerar_w			varchar(0001);


BEGIN

cd_conta_contabil_ww		:= ' ' || cd_conta_parametro_p || ' ';
cd_conta_contabil_ww		:= replace(cd_conta_contabil_ww, ',', ' , ');
cd_conta_contabil_ww		:= replace(cd_conta_contabil_ww, '(', ' ');
cd_conta_contabil_ww		:= replace(cd_conta_contabil_ww, ')', ' ');
cd_conta_contabil_ww		:= replace(cd_conta_contabil_ww, '  ', ' ');

select	coalesce(max('S') , 'N')
into STRICT	ie_gerar_w

where	cd_conta_contabil_ww like '% ' || cd_conta_contabil_p || ' %';

return	ie_gerar_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
