CREATE OR REPLACE FUNCTION ctb_obter_se_existe_orc (cd_estabelecimento_p bigint, nr_seq_mes_ref_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'S';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w

where	exists (	SELECT	1
		from	ctb_orcamento
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	nr_seq_mes_ref		= nr_seq_mes_ref_p
		and	cd_centro_custo		= cd_centro_custo_p
		and	cd_conta_contabil	= cd_conta_contabil_p);

return	ie_retorno_w;

end;
