CREATE OR REPLACE FUNCTION ctb_obter_se_mantem_cc ( cd_estabelecimento_p bigint, cd_tipo_lote_contabil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_centro_custo_w	varchar(15);


BEGIN

select	coalesce(max(ie_centro_custo),'S')
into STRICT	ie_centro_custo_w
from	ctb_regra_agrup
where	cd_estabelecimento		= cd_estabelecimento_p
and	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p;

return ie_centro_custo_w;

END;
