CREATE OR REPLACE FUNCTION ctb_obter_se_mes_fechado_grid ( nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



dt_fechamento_w		timestamp;
dt_abertura_w			timestamp;
ie_fecha_estab_w		varchar(01);
cd_empresa_w			smallint;
ie_fechado_w			varchar(01);
ds_aberto_w			varchar(01);
ds_fechado_w			varchar(01);
qt_reg_w			bigint;


BEGIN

ds_aberto_w := substr(wheb_mensagem_pck.get_texto(339858),1,1);
ds_fechado_w := substr(wheb_mensagem_pck.get_texto(339860),1,1);

ie_fechado_w 			:= ds_aberto_w;

select	b.dt_fechamento,
	b.dt_abertura,
	b.cd_empresa,
	a.ie_fecha_estab
into STRICT	dt_fechamento_w,
	dt_abertura_w,
	cd_empresa_w,
	ie_fecha_estab_w
from	empresa a,
	ctb_mes_ref b
where	b.nr_sequencia	= nr_seq_mes_ref_p
and	a.cd_empresa 		= b.cd_empresa;

if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') or (coalesce(dt_abertura_w::text, '') = '') then
	ie_fechado_w := ds_fechado_w;
end if;

if (ie_fechado_w = ds_aberto_w) and (coalesce(cd_estabelecimento_p,0) > 0) and (ie_fecha_estab_w = 'S') then
	select count(*)
	into STRICT	qt_reg_w
	from	ctb_mes_ref_estab
	where	nr_seq_mes		= nr_seq_mes_ref_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
	if (qt_reg_w	> 0) then
		ie_fechado_w := ds_fechado_w;
	end if;
end if;

return ie_fechado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
