CREATE OR REPLACE FUNCTION ctb_obter_se_orc_lib ( cd_centro_custo_p bigint, cd_conta_contabil_p text, dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_liberado_w
from	ctb_orcamento
where	cd_conta_contabil				= cd_conta_contabil_p
and	cd_centro_custo					= cd_centro_custo_p
and	trunc(ctb_obter_mes_ref(nr_seq_mes_ref),'mm')	= trunc(dt_referencia_p,'mm')
and	cd_estabelecimento				= cd_estabelecimento_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
