CREATE OR REPLACE FUNCTION ctb_obter_situacao_mes ( cd_empresa_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



dt_fechamento_w			timestamp;
dt_abertura_w			timestamp;
dt_referencia_w			timestamp;
ie_fecha_estab_w			varchar(01);
cd_empresa_w			smallint;
ie_fechado_w			varchar(01);
nr_seq_mes_ref_w			bigint;
qt_reg_w				bigint;


BEGIN

ie_fechado_w 			:= 'A';

dt_referencia_w	:= trunc(dt_referencia_p,'mm');

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_mes_ref_w
from	ctb_mes_ref
where	cd_empresa	= cd_empresa_p
and	dt_referencia	= dt_referencia_w;

if (nr_seq_mes_ref_w <> 0) then
	begin
	select	b.dt_fechamento,
		b.dt_abertura,
		b.cd_empresa,
		a.ie_fecha_estab
	into STRICT	dt_fechamento_w,
		dt_abertura_w,
		cd_empresa_w,
		ie_fecha_estab_w
	from	empresa a,
		ctb_mes_ref b
	where	b.nr_sequencia	= nr_seq_mes_ref_w
	and	a.cd_empresa 		= b.cd_empresa;

	if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') or (coalesce(dt_abertura_w::text, '') = '') then
		ie_fechado_w := 'F';
	end if;

	if (ie_fechado_w = 'A') and (coalesce(cd_estabelecimento_p,0) > 0) and (ie_fecha_estab_w = 'S') then
		select count(*)
		into STRICT	qt_reg_w
		from	ctb_mes_ref_estab
		where	nr_seq_mes		= nr_seq_mes_ref_w
		and	cd_estabelecimento	= cd_estabelecimento_p;
		if (qt_reg_w	> 0) then
			ie_fechado_w := 'F';
		end if;
	end if;
	end;
end if;

return ie_fechado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
