SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_situacao_result ( cd_conta_contabil_p text, dt_vigencia_p timestamp, vl_saldo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classif_conta_w			varchar(40);
cd_grupo_w			bigint;
ie_retorno_w			varchar(1)	:= 'P';
ie_tipo_conta_w			varchar(1);
ie_tipo_w				varchar(1);


BEGIN

select	max(cd_grupo),
	max(ie_tipo)
into STRICT	cd_grupo_w,
	ie_tipo_conta_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_contabil_p;

select	max(ie_tipo)
into STRICT	ie_tipo_w
from	ctb_grupo_conta
where	cd_grupo		= cd_grupo_w;

if (ie_tipo_conta_w = 'T') then
	begin
	if (vl_saldo_p > 0) then
		ie_retorno_w	:= 'P';
	else
		ie_retorno_w	:= 'N';
	end if;

	if (ie_tipo_w = 'R') then
		begin
		cd_classif_conta_w	:= substr(ctb_obter_classif_conta(cd_conta_contabil_p, cd_classif_conta_w, dt_vigencia_p),1,1);
		if (cd_classif_conta_w = '3') then
			begin
			ie_retorno_w    := 'P';
			if (vl_saldo_p < 0) then
				ie_retorno_w    := 'N';
			end if;
		end;
		elsif (cd_classif_conta_w = '4') then
			ie_retorno_w    := 'N';
			if (vl_saldo_p < 0) then
				ie_retorno_w    := 'P';
			end if;
		end if;
		end;
	elsif (ie_tipo_w = 'C') then
		begin
		ie_retorno_w	:= 'P';
		if (vl_saldo_p < 0) then
			ie_retorno_w    := 'N';
		end if;
		end;
	elsif (ie_tipo_w = 'D') then
		begin
		ie_retorno_w	:= 'N';
		if (vl_saldo_p < 0) then
			ie_retorno_w    := 'P';
		end if;
		end;
	end if;
	end;
else
	begin
	if (ie_tipo_w = 'R') then
		begin
		cd_classif_conta_w	:= substr(ctb_obter_classif_conta(cd_conta_contabil_p, cd_classif_conta_w, dt_vigencia_p),1,1);
		if (cd_classif_conta_w = '3') then
			begin
			ie_retorno_w    := 'R';
			if (vl_saldo_p < 0) then
				ie_retorno_w    := 'D';
			end if;
		end;
		elsif (cd_classif_conta_w = '4') then
			ie_retorno_w    := 'D';
			if (vl_saldo_p < 0) then
				ie_retorno_w    := 'R';
			end if;
		end if;
		end;
	elsif (ie_tipo_w = 'C') then
		begin
		ie_retorno_w	:= 'R';
		if (vl_saldo_p < 0) then
			ie_retorno_w    := 'D';
		end if;
		end;
	elsif (ie_tipo_w = 'D') then
		begin
		ie_retorno_w	:= 'D';
		if (vl_saldo_p < 0) then
			ie_retorno_w    := 'R';
		end if;
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
