SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ctb_obter_situacao_saldo ( cd_conta_contabil_p text, vl_saldo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w		varchar(1);
ie_debito_credito_w	varchar(1);
cd_grupo_w		bigint;


BEGIN
ie_situacao_w	:= 'C';

select	coalesce(max(cd_grupo),0)
into STRICT	cd_grupo_w
from	conta_contabil
where	cd_conta_contabil	= cd_conta_contabil_p;
if (cd_grupo_w > 0) then
	select	ie_debito_credito
	into STRICT	ie_debito_credito_w
	from	ctb_grupo_conta
	where	cd_grupo = cd_grupo_w;

	if (vl_saldo_p > 0) then
		ie_situacao_w	:= ie_debito_credito_w;
	else
		ie_situacao_w	:= 'D';
		if (ie_debito_credito_w = 'D') then
			ie_situacao_w	:= 'C';
		end if;
	end if;
end if;

return ie_situacao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
