CREATE OR REPLACE FUNCTION ctb_obter_tipo_lote_contabil ( nr_lote_contabil_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
'c' - Código
'D' - Descrição
*/
cd_tipo_lote_contabil_w			tipo_lote_contabil.cd_tipo_lote_contabil%type;
ds_tipo_lote_contabil_w			varchar(255);
ds_retorno_w				varchar(255);



BEGIN

if (coalesce(nr_lote_contabil_p,0) != 0) then
	begin
	select	a.cd_tipo_lote_contabil,
		substr(obter_desc_expressao(a.cd_exp_desc,a.ds_tipo_lote_contabil),1,255)
	into STRICT	cd_tipo_lote_contabil_w,
		ds_tipo_lote_contabil_w
	from	tipo_lote_contabil a,
		lote_contabil b
	where	b.nr_lote_contabil 	= nr_lote_contabil_p
	and	a.cd_tipo_lote_contabil = b.cd_tipo_lote_contabil;

	if (ie_opcao_p = 'C') then
		ds_retorno_w := cd_tipo_lote_contabil_w;
	else
		ds_retorno_w := ds_tipo_lote_contabil_w;
	end if;

	end;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
