CREATE OR REPLACE FUNCTION ctb_obter_valores_lancamento ( nr_lote_contabil_p bigint, ie_debito_credito_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w ctb_movimento.vl_movimento%type;


BEGIN

if (ie_debito_credito_p = 'D')then
 select  coalesce(sum(vl_movimento),0)
 into STRICT    vl_retorno_w
 from w_ctb_lancamento
 where nr_lote_contabil = nr_lote_contabil_p
 and  (cd_conta_debito IS NOT NULL AND cd_conta_debito::text <> '');
else
 select  coalesce(sum(vl_movimento),0)
 into STRICT    vl_retorno_w
 from w_ctb_lancamento
 where nr_lote_contabil = nr_lote_contabil_p
 and  (cd_conta_credito IS NOT NULL AND cd_conta_credito::text <> '');
end if;


return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
