CREATE OR REPLACE FUNCTION ctb_obter_valor_orcado ( dt_referencia_p timestamp, cd_conta_contabil_p text, cd_centro_custo_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_orcado_w		double precision;
nr_seq_mes_ref_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_mes_ref_w
from	ctb_mes_ref
where	PKG_DATE_UTILS.start_of(dt_referencia, 'month', 0) = dt_referencia_p;

select	coalesce(max(vl_orcado),0)
into STRICT	vl_orcado_w
from	ctb_orcamento
where	cd_estabelecimento		= cd_estabelecimento_p
and	cd_centro_custo		= cd_centro_custo_p
and	cd_conta_contabil		= cd_conta_contabil_p
and	nr_seq_mes_ref		= nr_seq_mes_ref_w;

return	vl_orcado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
