CREATE OR REPLACE FUNCTION ctb_obter_variacao_acomp (vl_orcado_p bigint, vl_realizado_p bigint) RETURNS bigint AS $body$
DECLARE


vl_orcado_w		double precision;
vl_realizado_w		double precision;
vl_resultado_w		double precision;


BEGIN

vl_orcado_w	:= coalesce(vl_orcado_p,0);
vl_realizado_w	:= coalesce(vl_realizado_p,0)
;
if (vl_orcado_w = 0) then
	vl_resultado_w := dividir((vl_realizado_w - vl_orcado_w) * 100, vl_realizado_w);
	if (vl_realizado_w < 0) then
		vl_resultado_w := vl_resultado_w * -1;
	end if;
else
	vl_resultado_w := dividir((vl_realizado_w - vl_orcado_w) * 100, vl_orcado_w);
end if;

return	vl_resultado_w;

end;
