CREATE OR REPLACE FUNCTION ctb_obter_vl_tributo ( cd_conta_contabil_p text, ie_soma_diminui_p text, ie_tipo_valor_p text, cd_tributo_p bigint, cd_estabelecimento_p bigint, nr_seq_mes_ref_p bigint) RETURNS bigint AS $body$
DECLARE


/*
Tipos de valores
M - valor de movimento
S - valor de saldo
D - valor de débito
C - valor de crédito
*/
vl_resultado_w	double precision;


BEGIN

if (ie_tipo_valor_p = 'M') then

	select	sum(b.vl_movimento)
	into STRICT	vl_resultado_w
	from	ctb_saldo b
	where	b.cd_conta_contabil = cd_conta_contabil_p
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, b.cd_estabelecimento)
	and	b.nr_seq_mes_ref = nr_seq_mes_ref_p;

elsif (ie_tipo_valor_p = 'S') then

	select	sum(b.vl_saldo)
	into STRICT	vl_resultado_w
	from	ctb_saldo b
	where	b.cd_conta_contabil = cd_conta_contabil_p
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, b.cd_estabelecimento)
	and	b.nr_seq_mes_ref = nr_seq_mes_ref_p;

elsif (ie_tipo_valor_p = 'D') then

	select	sum(b.vl_debito)
	into STRICT	vl_resultado_w
	from	ctb_saldo b
	where	b.cd_conta_contabil = cd_conta_contabil_p
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, b.cd_estabelecimento)
	and	b.nr_seq_mes_ref = nr_seq_mes_ref_p;

elsif (ie_tipo_valor_p = 'C') then

	select	sum(b.vl_credito)
	into STRICT	vl_resultado_w
	from	ctb_saldo b
	where	b.cd_conta_contabil = cd_conta_contabil_p
	and	b.cd_estabelecimento = coalesce(cd_estabelecimento_p, b.cd_estabelecimento)
	and	b.nr_seq_mes_ref = nr_seq_mes_ref_p;

end if;

if (ie_soma_diminui_p = 'D') then
	vl_resultado_w := vl_resultado_w * (-1);
end if;

return	vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
