CREATE OR REPLACE FUNCTION ctb_zerar_ult_nivel_classif ( cd_classif_conta_p text, cd_classif_filtro_p text) RETURNS varchar AS $body$
DECLARE


cd_classif_w		varchar(40);
qt_tamanho_w		bigint;


BEGIN

cd_classif_w		:= cd_classif_conta_p;
qt_tamanho_w		:= length(cd_classif_filtro_p);
if (length(cd_classif_conta_p) > qt_tamanho_w) and (substr(cd_classif_conta_p,1,qt_tamanho_w) 	= cd_classif_filtro_p) then
	cd_classif_w	:= substr(cd_classif_conta_p,1,qt_tamanho_w) || '.000';
end if;

return cd_classif_w;

end;
