SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cus_obter_acesso_classif ( nm_usuario_p text, cd_classificacao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_perfil_w		bigint;
cd_perfil_ativo_w	bigint;
ie_atualizacao_w	varchar(1);
nm_usuario_lib_w	varchar(15);
qt_registro_w		bigint;

c01 CURSOR FOR
SELECT	a.cd_perfil,
	a.nm_usuario_lib,
	a.ie_permissao
from	classif_result_lib a
where	cd_classificacao = cd_classificacao_p
and	coalesce(cd_perfil, cd_perfil_ativo_w)		= cd_perfil_ativo_w
and	coalesce(nm_usuario_lib, nm_usuario_p)		= nm_usuario_p
order by coalesce(a.nm_usuario_lib,'A'),
	coalesce(cd_perfil,0);


BEGIN

ie_atualizacao_w	:= 'S';

select	count(*)
into STRICT	qt_registro_w
from	classif_result_lib a
where	a.cd_classificacao = cd_classificacao_p;

if (qt_registro_w > 0) then
	begin
	ie_atualizacao_w	:= 'N';
	begin
		cd_perfil_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
	exception when others then
		cd_perfil_ativo_w	:= 0;
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(278188);
	end;

	open C01;
	loop
	fetch C01 into
		cd_perfil_w,
		nm_usuario_lib_w,
		ie_atualizacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_atualizacao_w	:= ie_atualizacao_w;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

			select	count(*)
			into STRICT	qt_registro_w
			from	usuario_perfil
			where	cd_perfil	= cd_perfil_w
			and	nm_usuario	= nm_usuario_p;

			if (qt_registro_w = 0) then
				ie_atualizacao_w	:= 'N';
			end if;
		end if;


		end;
	end loop;
	close C01;

	end;
end if;

return	ie_atualizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
