CREATE OR REPLACE FUNCTION cus_obter_custo_proc_mat ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


cd_tabela_custo_w			bigint;
cd_tipo_tabela_custo_w		bigint;
dt_referencia_w			timestamp;
vl_retorno_w			double precision;


BEGIN

dt_referencia_w		:= trunc(dt_referencia_p,'mm');
cd_tipo_tabela_custo_w	:= 3;

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	cd_tipo_tabela_custo_w	:= 4;
end if;



select	max(cd_tabela_custo)
into STRICT	cd_tabela_custo_w
from	tabela_custo
where	cd_estabelecimento		= cd_estabelecimento_p
and	cd_tipo_tabela_custo	= cd_tipo_tabela_custo_w
and	dt_mes_referencia		= dt_referencia_w;

if (cd_tabela_custo_w IS NOT NULL AND cd_tabela_custo_w::text <> '') then
	begin
	if (cd_tipo_tabela_custo_w = 3) then
		begin

		if (coalesce(nr_seq_proc_interno_p,0) = 0) then
			begin
			select	coalesce(max(vl_cotado),0)
			into STRICT	vl_retorno_w
			from	custo_procedimento
			where	cd_tabela_custo		= cd_tabela_custo_w
			and	cd_estabelecimento		= cd_estabelecimento_p
			and	cd_procedimento		= cd_procedimento_p
			and	ie_origem_proced		= ie_origem_proced_p;
			end;
		else
			begin
			select	coalesce(max(vl_cotado),0)
			into STRICT	vl_retorno_w
			from	custo_procedimento
			where	cd_tabela_custo		= cd_tabela_custo_w
			and	cd_estabelecimento		= cd_estabelecimento_p
			and	nr_seq_proc_interno	= nr_seq_proc_interno_p;
			end;
		end if;

		end;
	elsif (cd_tipo_tabela_custo_w = 4) then
		begin
		select	coalesce(max(vl_cotado_consumo),0)
		into STRICT	vl_retorno_w
		from	custo_material
		where	cd_tabela_custo	= cd_tabela_custo_w
		and	cd_estabelecimento	= cd_estabelecimento_p
		and	cd_material	= cd_material_p;
		end;

	end if;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
