CREATE OR REPLACE FUNCTION cus_obter_dados_ng ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_conta_contabil_w		varchar(40);
cd_natureza_gasto_w		numeric(20);
ds_natureza_gasto_w		varchar(255);
ds_retorno_w			varchar(255);
nr_seq_gng_w			bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(cd_natureza_gasto),
		max(ds_natureza_gasto),
		max(cd_conta_contabil),
		max(nr_seq_gng)
	into STRICT	cd_natureza_gasto_w,
		ds_natureza_gasto_w,
		cd_conta_contabil_w,
		nr_seq_gng_w
	from	natureza_gasto
	where	nr_sequencia	= nr_sequencia_p;


	if (ie_opcao_p	= 'C') then
		ds_retorno_w	:= cd_natureza_gasto_w;
	elsif (ie_opcao_p	= 'D') then
		ds_retorno_w	:= ds_natureza_gasto_w;
	elsif (ie_opcao_p	= 'DC') then
		ds_retorno_w	:= substr(ds_natureza_gasto_w || ' - ' || cd_natureza_gasto_w,1,255);
	elsif (ie_opcao_p	= 'CO') then
		ds_retorno_w	:= cd_conta_contabil_w;
	elsif (ie_opcao_p	= 'G') then
		ds_retorno_w	:= nr_seq_gng_w;
	elsif (ie_opcao_p	= 'DG') then
		ds_retorno_w	:= substr(CUS_OBTER_DESC_GNG(nr_seq_gng_w),1,255);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
