CREATE OR REPLACE FUNCTION cus_obter_desc_ficha_tecnica ( nr_ficha_tecnica_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w			bigint;
ie_origem_proced_w			bigint;
nr_seq_proc_interno_w			bigint;
nr_seq_exame_w				bigint;
cd_material_w				bigint;
ds_retorno_w				varchar(255);

BEGIN

begin
select	cd_procedimento,
	ie_origem_proced,
	nr_seq_proc_interno,
	nr_seq_exame,
	cd_material
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w,
	nr_seq_exame_w,
	cd_material_w
from	ficha_tecnica
where	nr_ficha_tecnica	= nr_ficha_tecnica_p;
exception when no_data_found then
	ds_retorno_w	:= '';
end;

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	ds_retorno_w	:= substr(obter_desc_material(cd_material_w),1,255);
elsif (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
	ds_retorno_w	:= substr(obter_descricao_procedimento(cd_procedimento_w, ie_origem_proced_w),1,255);
elsif (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
	ds_retorno_w	:= substr(obter_desc_proc_exame(nr_seq_proc_interno_w),1,100);
elsif (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
	ds_retorno_w	:= substr(obter_desc_exame(nr_seq_exame_w),1,255);
end if;




return	ds_retorno_w;

end;
$body$
