CREATE OR REPLACE FUNCTION cus_obter_desc_tipo_tabela ( cd_tipo_tabela_custo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			dic_expressao.ds_expressao_br%type;


BEGIN

if (coalesce(cd_tipo_tabela_custo_p,0) <> 0) then

	select	substr(coalesce(obter_desc_expressao(cd_exp_desc),a.ds_tipo_tabela_custo),1,255)
	into STRICT	ds_retorno_w
	from	tipo_tabela_custo a
	where	a.cd_tipo_tabela_custo	= cd_tipo_tabela_custo_p;
end if;

return ds_retorno_w;

end;
