CREATE OR REPLACE FUNCTION cus_obter_margem_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_receita_total_w		double precision;
vl_custo_var_mc_w		double precision;
vl_retorno_w			double precision;
vl_custo_variavel_w		double precision;
vl_custo_total_w		double precision;
vl_custo_w			double precision;


BEGIN

if (ie_opcao_p = 'C') then
	begin
	select	coalesce(sum(a.vl_custo),0) vl_custo
	into STRICT	vl_custo_w
	from	conta_paciente_resumo a
	where	a.nr_interno_conta	= nr_interno_conta_p;
	vl_retorno_w	:= vl_custo_w;
	end;
else
	begin

	select	coalesce(sum(a.vl_procedimento),0) + coalesce(sum(a.vl_material),0) vl_receita,
		coalesce(sum(a.vl_custo_var_mc),0) vl_custo_var_mc
	into STRICT	vl_receita_total_w,
		vl_custo_var_mc_w
	from	conta_paciente_resumo a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(qt_exclusao_custo,0) = 0;

	select	coalesce(sum(a.vl_custo_variavel),0)
	into STRICT	vl_custo_variavel_w
	from	cus_conta_pac_mc a
	where	a.nr_interno_conta	= nr_interno_conta_p;

	vl_custo_total_w	:= vl_custo_var_mc_w + vl_custo_variavel_w;

	if (ie_opcao_p = 'V') then
		vl_retorno_w	:= vl_receita_total_w - vl_custo_total_w;
	elsif (ie_opcao_p = 'P') then
		vl_retorno_w	:= (dividir((vl_receita_total_w - vl_custo_total_w),vl_receita_total_w) * 100);
	end if;
	end;
end if;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
