CREATE OR REPLACE FUNCTION cus_obter_perc_item_mc ( cd_estabelecimento_p bigint, nr_seq_item_mc_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_vigencia_w			timestamp;
pr_retorno_w			double precision	:= 0;

c01 CURSOR FOR
SELECT	coalesce(pr_item,0)
from	cus_item_mc_regra a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.nr_seq_item		= nr_seq_item_mc_p
and (a.dt_inicio_vig <= dt_vigencia_w and coalesce(a.dt_fim_vig,clock_timestamp()) >= dt_vigencia_w)
order by a.dt_inicio_vig;


BEGIN

dt_vigencia_w	:= coalesce(dt_vigencia_p, clock_timestamp());

open C01;
loop
fetch C01 into
	pr_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	pr_retorno_w	:= pr_retorno_w;
	end;
end loop;
close C01;

return	pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
