SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cus_obter_perc_total_result ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, cd_centro_controle_p bigint, cd_classificacao_p bigint, ie_centro_resultado_p text, ie_tipo_centro_controle_p text) RETURNS bigint AS $body$
DECLARE


cd_empresa_w			smallint;
pr_total_classif_w			double precision	:= 0;
vl_total_classif_mes_w		double precision;
vl_mes_w				double precision;


BEGIN

cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);

if (cd_classificacao_p IS NOT NULL AND cd_classificacao_p::text <> '') then
	begin
	if (cd_centro_controle_p = 99999999) then
		begin
		pr_total_classif_w	:= 100;
		end;
	else
		begin
		select	sum(vl_mes)
		into STRICT	vl_mes_w
		from	centro_controle c,
			classif_result b,
			resultado_centro_controle a
		where	a.cd_estabelecimento	= c.cd_estabelecimento
		and	a.cd_centro_controle	= c.cd_centro_controle
		and	a.ie_classif_conta		= b.cd_classificacao
		and	c.ie_situacao		= 'A'
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	a.cd_tabela_custo		= cd_tabela_custo_p
		and	c.cd_centro_controle	= cd_centro_controle_p
		and	b.cd_empresa		= cd_empresa_w
		and	b.cd_classificacao		= cd_classificacao_p;

		select	sum(vl_mes)
		into STRICT	vl_total_classif_mes_w
		from	centro_controle c,
			classif_result b,
			resultado_centro_controle a
		where	a.cd_estabelecimento	= c.cd_estabelecimento
		and	a.cd_centro_controle	= c.cd_centro_controle
		and	a.ie_classif_conta		= b.cd_classificacao
		and	c.ie_situacao		= 'A'
		and	((c.ie_tipo_centro_controle	= ie_tipo_centro_controle_p) or (coalesce(ie_tipo_centro_controle_p::text, '') = ''))
		and	c.ie_centro_resultado	= coalesce(ie_centro_resultado_p,'N')
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	a.cd_tabela_custo		= cd_tabela_custo_p
		and	b.cd_empresa		= cd_empresa_w
		and	b.cd_classificacao		= cd_classificacao_p;

		pr_total_classif_w	:= (dividir(vl_mes_w, vl_total_classif_mes_w) * 100);
		end;
	end if;
	end;
end if;

return pr_total_classif_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
