SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION cus_obter_receita_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_receita_total_w			double precision;
vl_custo_var_mc_w			double precision;
pr_retorno_w			double precision	:= 100;
vl_custo_variavel_w		double precision;
vl_custo_total_w			double precision;


BEGIN

select	coalesce(sum(a.vl_procedimento),0) + coalesce(sum(a.vl_material),0) vl_receita,
	coalesce(sum(a.vl_custo_var_mc),0) vl_custo_var_mc
into STRICT	vl_receita_total_w,
	vl_custo_var_mc_w
from	conta_paciente_resumo a
where	a.nr_interno_conta	= nr_interno_conta_p
and	coalesce(qt_exclusao_custo,0) = 0;

select	coalesce(sum(a.vl_custo_variavel),0)
into STRICT	vl_custo_variavel_w
from	cus_conta_pac_mc a
where	a.nr_interno_conta	= nr_interno_conta_p;

vl_custo_total_w	:= vl_custo_var_mc_w + vl_custo_variavel_w;

if (ie_opcao_p = 'CV') then
	pr_retorno_w	:= dividir(vl_custo_var_mc_w * 100,vl_receita_total_w);
elsif (ie_opcao_p = 'CDV') then
	pr_retorno_w	:= dividir(vl_custo_variavel_w * 100,vl_receita_total_w);
elsif (ie_opcao_p = 'VM') then
	pr_retorno_w	:= dividir((vl_receita_total_w - vl_custo_total_w) * 100,vl_receita_total_w);
elsif (ie_opcao_p = 'PM') then
	pr_retorno_w	:= 0;
elsif (ie_opcao_p = 'CP') then
	begin
	select	coalesce(sum(a.vl_custo),0) vl_custo
	into STRICT	vl_custo_total_w
	from	conta_paciente_resumo a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_material::text, '') = ''
	and	coalesce(qt_exclusao_custo,0) = 0;

	pr_retorno_w	:= dividir((vl_receita_total_w - vl_custo_total_w) * 100,vl_receita_total_w);
	end;
elsif (ie_opcao_p = 'RL') then
	begin
	select	((coalesce(sum(a.vl_procedimento),0) + coalesce(sum(a.vl_material),0)) - coalesce(sum(a.vl_custo),0)) vl_custo
	into STRICT	vl_custo_total_w
	from	conta_paciente_resumo a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(qt_exclusao_custo,0) = 0;

	pr_retorno_w	:= dividir(vl_custo_total_w * 100,vl_receita_total_w);
	end;
else
	if (vl_receita_total_w = 0) then
		pr_retorno_w	:= 0;
	end if;
end if;

return	coalesce(pr_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cus_obter_receita_conta ( nr_interno_conta_p bigint, ie_opcao_p text) FROM PUBLIC;

