CREATE OR REPLACE FUNCTION cus_obter_rec_liquida ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, cd_centro_controle_p bigint, ie_centro_resultado_p text, ie_tipo_centro_controle_p text, ie_unidade_negocio_p bigint DEFAULT null) RETURNS bigint AS $body$
DECLARE


cd_empresa_w				smallint;
vl_receita_liquida_w			double precision;


BEGIN

select	max(obter_empresa_estab(cd_estabelecimento_p))
into STRICT	cd_empresa_w
;

if (cd_centro_controle_p = 99999999) then
	begin
	if (ie_tipo_centro_controle_p = 2) then
		select	sum(vl_mes)
		into STRICT	vl_receita_liquida_w
		from	centro_controle c,
			classif_result b,
			resultado_centro_controle a
		where	a.cd_estabelecimento		= c.cd_estabelecimento
		and	a.cd_centro_controle		= c.cd_centro_controle
		and	a.ie_classif_conta		= b.cd_classificacao
		and	c.ie_situacao			= 'A'
		and	b.ie_receita_liquida		= 'S'
		and	c.ie_tipo_centro_controle	= ie_tipo_centro_controle_p
		and	c.ie_centro_resultado	= coalesce(ie_centro_resultado_p,'N')
		and	a.cd_estabelecimento		= cd_estabelecimento_p
		and	a.cd_tabela_custo		= cd_tabela_custo_p
		and	((coalesce(ie_unidade_negocio_p, 0) = 0) or (substr(cus_obter_se_centro_unid_neg(c.cd_estabelecimento, c.cd_centro_controle, ie_unidade_negocio_p),1,1) = 'S'))
		and	b.cd_empresa			= cd_empresa_w;
	else
		select	sum(vl_mes)
		into STRICT	vl_receita_liquida_w
		from	centro_controle c,
			classif_result b,
			resultado_centro_controle a
		where	a.cd_estabelecimento		= c.cd_estabelecimento
		and	a.cd_centro_controle		= c.cd_centro_controle
		and	a.ie_classif_conta		= b.cd_classificacao
		and	c.ie_situacao			= 'A'
		and	b.ie_receita_liquida		= 'S'
		and	c.ie_tipo_centro_controle <> ie_tipo_centro_controle_p
		and	c.ie_centro_resultado	= coalesce(ie_centro_resultado_p,'N')
		and	a.cd_estabelecimento		= cd_estabelecimento_p
		and	a.cd_tabela_custo		= cd_tabela_custo_p
		and	((coalesce(ie_unidade_negocio_p, 0) = 0) or (substr(cus_obter_se_centro_unid_neg(c.cd_estabelecimento, c.cd_centro_controle, ie_unidade_negocio_p),1,1) = 'S'))
		and	b.cd_empresa			= cd_empresa_w;
	end if;
	end;	
else
	select	sum(vl_mes)
	into STRICT	vl_receita_liquida_w
	from	centro_controle c,
		classif_result b,
		resultado_centro_controle a
	where	a.cd_estabelecimento			= c.cd_estabelecimento
	and	a.cd_centro_controle			= c.cd_centro_controle
	and	a.ie_classif_conta			= b.cd_classificacao
	and	c.ie_situacao				= 'A'
	and	b.ie_receita_liquida			= 'S'
	and	((c.ie_tipo_centro_controle		= ie_tipo_centro_controle_p) or (coalesce(ie_tipo_centro_controle_p::text, '') = ''))
	and	c.ie_centro_resultado		= coalesce(ie_centro_resultado_p,'N')
	and	a.cd_estabelecimento			= cd_estabelecimento_p
	and	a.cd_tabela_custo			= cd_tabela_custo_p
	and	c.cd_centro_controle			= cd_centro_controle_p
	and	((coalesce(ie_unidade_negocio_p, 0) = 0) or (substr(cus_obter_se_centro_unid_neg(c.cd_estabelecimento, c.cd_centro_controle, ie_unidade_negocio_p),1,1) = 'S'))
	and	b.cd_empresa				= cd_empresa_w;
end if;

return vl_receita_liquida_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION cus_obter_rec_liquida ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, cd_centro_controle_p bigint, ie_centro_resultado_p text, ie_tipo_centro_controle_p text, ie_unidade_negocio_p bigint DEFAULT null) FROM PUBLIC;

