CREATE OR REPLACE FUNCTION cus_obter_regra_custo_mat ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
cd_material_w			bigint;
cd_retorno_w			bigint;

c01 CURSOR FOR
SELECT	a.cd_tabela_preco
from	cus_regra_custo_material a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(a.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w) 	= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and	coalesce(a.cd_material, cd_material_w)			= cd_material_w
order by coalesce(a.cd_material,0),
	 coalesce(a.cd_classe_material,0),
	 coalesce(a.cd_subgrupo_material,0),
	 coalesce(a.cd_grupo_material,0);


BEGIN

cd_material_w	:= cd_material_p;

select	max(a.cd_grupo_material),
	max(a.cd_subgrupo_material),
	max(a.cd_classe_material)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material	= cd_material_p;

open C01;
loop
fetch C01 into
	cd_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_retorno_w	:= cd_retorno_w;
	end;
end loop;
close C01;

return	cd_retorno_w;

end;
$body$
