CREATE OR REPLACE FUNCTION cus_obter_se_centro_usuario ( cd_estabelecimento_p bigint, cd_centro_controle_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_centro_superior_w		integer;
ie_liberado_w			varchar(01)	:= 'N';
qt_usuario_w			integer	:= 0;

nm_usuario_w			usuario.nm_usuario%type;


BEGIN

begin
	select	'S'
	into STRICT	ie_liberado_w
	from	centro_controle a
	where	a.cd_centro_controle	= cd_centro_controle_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.nm_pessoa_resp		= nm_usuario_p  LIMIT 1;
exception
when others then
	ie_liberado_w := 'N';
end;

if (ie_liberado_w = 'N') then
	begin
		select	'S'
		into STRICT	ie_liberado_w
		from	centro_controle_usuario a
		where	a.nm_usuario_lib	= nm_usuario_p
		and	a.cd_centro_controle	= cd_centro_controle_p
		and	a.cd_estabelecimento	= cd_estabelecimento_p  LIMIT 1;
	exception
	when others then
		ie_liberado_w := 'N';
	end;

	if (ie_liberado_w = 'N') then
		begin
		begin
		select	'S'
		into STRICT	ie_liberado_w
		from	centro_controle b,
			centro_controle_usuario a
		where	a.cd_centro_controle	= b.cd_centro_controle_pai
		and	a.cd_estabelecimento	= b.cd_estabelecimento
		and	a.nm_usuario_lib	= nm_usuario_p
		and	b.cd_centro_controle	= cd_centro_controle_p
		and	b.cd_estabelecimento	= cd_estabelecimento_p  LIMIT 1;

		exception
		when others then
			ie_liberado_w := 'N';
		end;

		end;
	end if;

end if;
return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
