CREATE OR REPLACE FUNCTION cus_obter_se_importar_orc ( cd_estabelecimento_p bigint, nr_seq_gng_p bigint, nr_seq_ng_p bigint, ie_tipo_importacao_p bigint, cd_centro_controle_p bigint) RETURNS varchar AS $body$
DECLARE


ie_importar_w			varchar(1);
ie_retorno_w			varchar(1);

c01 CURSOR FOR
	SELECT	ie_importar
	from	cus_regra_imp_contab
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(nr_seq_gng, nr_seq_gng_p)		        = nr_seq_gng_p
	and	coalesce(nr_seq_ng, nr_seq_ng_p)			= nr_seq_ng_p
	and	coalesce(ie_tipo_importacao, ie_tipo_importacao_p)	= ie_tipo_importacao_p
	and	coalesce(cd_centro_controle, cd_centro_controle_p)	= cd_centro_controle_p
	order	by	coalesce(nr_seq_ng,0),
			coalesce(nr_seq_gng,0),
			coalesce(cd_centro_controle,0);


BEGIN

open C01;
loop
fetch C01 into
	ie_importar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_retorno_w	:= ie_importar_w;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
