CREATE OR REPLACE FUNCTION cus_obter_se_tipo_gasto ( ie_tipo_gasto_p text, ie_filtro_p text) RETURNS varchar AS $body$
DECLARE


ie_imprime_w					varchar(1)	:= 'S';

/* Ie filtro
C - Custos Fixo e Variavel (Imprime Tipo de gasto F e V)
F - Custo fixo
V - Custo vari√°vel
R - Receita

*/
BEGIN

if (coalesce(ie_filtro_p,'0') <> '0') and (ie_tipo_gasto_p <> ie_filtro_p) then
	ie_imprime_w	:= 'N';

	if (ie_filtro_p = 'C') and (ie_tipo_gasto_p in ('V','F')) then
		ie_imprime_w	:= 'S';
	end if;

end if;

return	ie_imprime_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
