CREATE OR REPLACE FUNCTION cus_obter_taxa_custo ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, dt_mes_referencia_p timestamp, cd_centro_controle_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


cd_tabela_custo_w			bigint;
vl_retorno_w			double precision;
vl_taxa_custo_w			double precision;
vl_custo_w			double precision;
qt_disponibilidade_w		double precision;

BEGIN

cd_tabela_custo_w			:= cd_tabela_custo_p;

if (coalesce(cd_tabela_custo_p,0) = 0) and (dt_mes_referencia_p IS NOT NULL AND dt_mes_referencia_p::text <> '') then

	select	coalesce(max(cd_tabela_custo),0)
	into STRICT	cd_tabela_custo_w
	from	tabela_custo
	where	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_tipo_tabela_custo	= 7
	and	dt_mes_referencia		= dt_mes_referencia_p;

end if;

select	coalesce(sum(vl_custo),0),
	coalesce(sum(vl_taxa_custo),0),
	coalesce(avg(qt_disponibilidade),0)
into STRICT	vl_custo_w,
	vl_taxa_custo_w,
	qt_disponibilidade_w
from	taxa_custo
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_tabela_custo	= cd_tabela_custo_w
and	cd_centro_controle	= cd_centro_controle_p;

if (ie_opcao_p = 'V') then
	vl_retorno_w	:= vl_custo_w;
elsif (ie_opcao_p = 'T') then
	vl_retorno_w	:= vl_taxa_custo_w;
elsif (ie_opcao_p = 'Q') then
	vl_retorno_w	:= qt_disponibilidade_w;
end if;

return	vl_retorno_w;

end;
$body$
