CREATE OR REPLACE FUNCTION dados_coluna_mapa_estoque ( nr_seq_mapa_p bigint, ie_entrada_saida_p text, ie_coluna_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* ie_retorno_p
	L = Linha
	S = Sequencia
	I = Entrada/Saida
	C = Coluna
	D = Descrição */
ie_linha_w			varchar(10);
nr_sequencia_w		varchar(10);
ie_entrada_saida_w		varchar(2);
nr_seq_coluna_w		varchar(2);
ds_coluna_w			varchar(15);
ds_retorno_w			varchar(15);


BEGIN

select	a.ie_linha,
	a.nr_sequencia,
	a.ie_entrada_saida,
	a.nr_seq_coluna,
	a.ds_coluna
into STRICT	ie_linha_w,
	nr_sequencia_w,
	ie_entrada_saida_w,
	nr_seq_coluna_w,
	ds_coluna_w
from (SELECT	row_number() OVER () AS ie_linha,
	nr_sequencia nr_sequencia,
	ie_entrada_saida ie_entrada_saida,
	nr_seq_coluna nr_seq_coluna,
	ds_coluna ds_coluna
from	mapa_est_item
where	nr_seq_mapa = nr_seq_mapa_p) a
where	a.nr_seq_coluna = ie_coluna_p
order by a.ie_entrada_saida, a.nr_seq_coluna;

if (ie_retorno_p = 'L') then
	ds_retorno_w	:= ie_linha_w;
elsif (ie_retorno_p = 'S') then
	ds_retorno_w	:= nr_sequencia_w;
elsif (ie_retorno_p = 'I') then
	ds_retorno_w	:= ie_entrada_saida_w;
elsif (ie_retorno_p = 'C') then
	ds_retorno_w	:= nr_seq_coluna_w;
elsif (ie_retorno_p = 'D') then
	ds_retorno_w	:= ds_coluna_w;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
