CREATE OR REPLACE FUNCTION dar_existe_restricao_atrib_qb (NM_TABELA_P text, NM_ATRIBUTO_P text, NR_SEQ_VISAO_P bigint) RETURNS varchar AS $body$
DECLARE

	EXISTE_RESTRICAO bigint;

BEGIN
    SELECT COUNT(*)
    INTO STRICT EXISTE_RESTRICAO
    FROM TABELA_ATRIB_REGRA
    WHERE NM_TABELA = NM_TABELA_P
    AND NM_ATRIBUTO = NM_ATRIBUTO_P
    AND NR_SEQ_VISAO = NR_SEQ_VISAO_P
    AND (NM_USUARIO = wheb_usuario_pck.get_nm_usuario
        OR CD_ESTABELECIMENTO = wheb_usuario_pck.get_cd_estabelecimento 
        OR CD_PERFIL = wheb_usuario_pck.get_cd_perfil)
    AND (IE_VISIBLE = 'N' OR IE_ENABLE = 'N');

	IF (EXISTE_RESTRICAO > 0) THEN
		RETURN 'S';
	ELSE
		RETURN 'N';
	END IF;	

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
