SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dar_valida_acesso_app (nr_sequencia_p dar_app.nr_sequencia%type, nm_usuario_p dar_app.nm_usuario_nrec%type) RETURNS varchar AS $body$
DECLARE

   ie_permite_w varchar(1);


BEGIN

   -- Validacao a nivel de criacao do APP
   select CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN  'N'  ELSE 'S' END
     into STRICT ie_permite_w
     from dar_app a
    where nr_sequencia = nr_sequencia_p
      and a.nm_usuario_nrec =
          coalesce(nm_usuario_p, wheb_usuario_pck.get_nm_usuario);

   if (ie_permite_w = 'N') then

      select CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN  'N'  ELSE 'S' END 
        into STRICT ie_permite_w
        from dar_app_shared a
       where a.nr_seq_app = nr_sequencia_p
         and ((a.nm_usuario_lib = coalesce(nm_usuario_p, wheb_usuario_pck.get_nm_usuario)) -- Validacao a nivel de liberacao por usuario do app
             or (a.cd_perfil = wheb_usuario_pck.get_cd_perfil) -- Validacao a nivel de liberacao por perfil de usuario
             or (a.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento)); -- Validacao a nivel de liberacao por estabelecimento
   end if;

   -- Retorno de liberacao
   return(ie_permite_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
