CREATE OR REPLACE FUNCTION dar_valida_acesso_query (nr_sequencia_p filter_query_dar.nr_sequencia%type, nm_usuario_p filter_query_dar.nm_usuario_nrec%type) RETURNS varchar AS $body$
DECLARE

   ie_permite_w varchar(1);


BEGIN

	select CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN  'N'  ELSE 'S' END
	into STRICT ie_permite_w
	from dar_query_filter_users a
	where a.nr_seq_filter = nr_sequencia_p
	and a.nm_usuario_comp = coalesce(nm_usuario_p, wheb_usuario_pck.get_nm_usuario);

	if (ie_permite_w = 'N') then
		-- Validacao a nivel de liberacao por perfil de usuario
		select CASE WHEN coalesce(max(a.nr_sequencia)::text, '') = '' THEN  'N'  ELSE 'S' END
		into STRICT ie_permite_w
		from dar_query_filter_users a
		where a.nr_seq_filter = nr_sequencia_p
		and a.cd_perfil = wheb_usuario_pck.get_cd_perfil;
	end if;

    return(ie_permite_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
