CREATE OR REPLACE FUNCTION dar_valida_filtros_dashboard (nr_sequencia_p dar_dashboard.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

   ie_filtered_w varchar(1);
   ie_permite_w bigint;


BEGIN
	
	select count(*)
	into STRICT ie_permite_w
	from dar_dashboard_filter
	where nr_seq_dashboard = nr_sequencia_p;
	
   -- Validacao
   if (coalesce(ie_permite_w, 0) = 0) then
      ie_filtered_w := 'S';
   else
      ie_filtered_w := 'N';
   end if;

      -- Retorno
   return(ie_filtered_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
