SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dar_verifica_perm_template_ld ( nr_seq_template_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 			varchar(1):='N';
qt_liberado_perfil_w	bigint := 0;
qt_liberado_outros_perfis_w	bigint := 0;


BEGIN

if (nr_seq_template_p IS NOT NULL AND nr_seq_template_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_liberado_perfil_w
	from	perfil_item_pront a,
	        perfil_item_pront_template b
	where	a.nr_sequencia = b.nr_seq_item_perfil
	and	a.cd_perfil    = wheb_usuario_pck.get_cd_perfil
	and	b.nr_seq_template  = nr_seq_template_p;

	select	count(*)
	into STRICT	qt_liberado_outros_perfis_w
	from	perfil_item_pront a,
	        perfil_item_pront_template b
	where	a.nr_sequencia = b.nr_seq_item_perfil
	and	b.nr_seq_template  = nr_seq_template_p;

	if (qt_liberado_perfil_w > 0 or qt_liberado_outros_perfis_w = 0) then
		begin
		ds_retorno_w := 'S';
		end;
	else
		begin
		ds_retorno_w := 'N';
		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION dar_verifica_perm_template_ld ( nr_seq_template_p bigint ) FROM PUBLIC;

