SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dashboard_permission (nr_seq_dashboard_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permission_type_w		varchar(5);
ie_qt_permissions_w			bigint;
nm_user_w					varchar(255);
nm_new_user_w				varchar(255);


BEGIN

	ie_permission_type_w := 'F';
	nm_user_w := obter_usuario_ativo;

	select	nm_usuario_nrec
	into STRICT	nm_new_user_w
	from	dashboard_base
	where	nr_sequencia = nr_seq_dashboard_p;

	if (nm_new_user_w = nm_user_w) then
	begin
		ie_permission_type_w := 'T';
	end;
	else
	begin

		select	count(nr_sequencia)
		into STRICT	ie_qt_permissions_w
		from	dashboard_permissions
		where	nr_seq_dashboard = nr_seq_dashboard_p
		and		((nm_user_permission = nm_user_w) or (cd_profile_permission IS NOT NULL AND cd_profile_permission::text <> '' AND obter_perfil_ativo = cd_profile_permission));

		if (ie_qt_permissions_w > 0) then
			ie_permission_type_w := 'T';
		end if;

	end;
	end if;


return	ie_permission_type_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
