SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION data_model_json_preview ( data_model_id_p data_model.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


object_name_w data_model.object_name%type;
json_model philips_json;
json_record philips_json;

c01 CURSOR FOR
SELECT alias_name, null value
from data_model_columns
where data_model_id = data_model_id_p;

BEGIN
json_model := philips_json();
json_record := philips_json();

for c01_w in c01 loop
    json_record.put(c01_w.alias_name, c01_w.value);
end loop;

select max(object_name)
into STRICT object_name_w
from data_model
where nr_sequencia = data_model_id_p;

json_model.put(object_name_w, json_record.to_json_value());

return json_model.to_char(false);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
