CREATE OR REPLACE FUNCTION date_as_varchar2 ( dt_reference_p timestamp, ds_mask_p text, nm_usuario_p text default null, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_masked_date_w        varchar(100);
cd_estabelecimento_w    estabelecimento.cd_estabelecimento%type;
nm_usuario_w            usuario.nm_usuario%type;
cd_funcao_ativa_w       funcao.cd_funcao%type;

-- In order to know which masks you can use, use this query:

-- select * from locale_formats order by id_mask;
BEGIN
    cd_estabelecimento_w    := coalesce(cd_estabelecimento_p, obter_estabelecimento_ativo);
    nm_usuario_w            := coalesce(nm_usuario_p, obter_usuario_ativo);
    cd_funcao_ativa_w       := obter_funcao_ativa;

    begin
        ds_masked_date_w :=
            case cd_funcao_ativa_w
                when PFCS_PCK_CONSTANTS.CD_FUNC_ENTERPRISE_DEMAND
                    then pkg_date_formaters_tz.to_varchar(dt_reference_p,ds_mask_p,cd_estabelecimento_w,nm_usuario_w,establishment_timezone_utils.getTimeZone)
                else pkg_date_formaters.to_varchar(dt_reference_p,ds_mask_p,cd_estabelecimento_w,nm_usuario_w)
            end;
    exception
        when others then
            ds_masked_date_w := null;
    end;

    return	ds_masked_date_w;
end;
