CREATE OR REPLACE FUNCTION dc_obter_conversao_interna_int ( cd_cgc_p text, nm_tabela_p text, nm_atributo_p text, cd_externo_p text, ie_sistema_externo_p text) RETURNS varchar AS $body$
DECLARE

  cd_interno_w varchar(80);


BEGIN

  cd_interno_w := '';

  begin
  
    select max(cd_interno)
    into STRICT cd_interno_w
    from conversao_meio_externo
    where ((coalesce(cd_cgc_p::text, '') = '')
    or (cd_cgc                = cd_cgc_p))
    and upper(nm_tabela)      = upper(nm_tabela_p)
    and upper(nm_atributo)    = upper(nm_atributo_p)
    and ((ie_sistema_externo  = ie_sistema_externo_p)
    or (coalesce(ie_sistema_externo_p::text, '') = ''))
    and cd_externo            = cd_externo_p;

  exception
  when others then
    cd_interno_w := '';
  end;

if (coalesce(cd_interno_w::text, '') = '') or (cd_interno_w = '') then
  cd_interno_w   := null;
end if;

return cd_interno_w;

end;
