CREATE OR REPLACE FUNCTION desab_item_regra_score_flex_ii ( nr_seq_item_p bigint, nr_seq_escala_p bigint) RETURNS varchar AS $body$
DECLARE


nr_item_desabilitar_w	bigint;
qt_reg_escala_ii_w		bigint;
nr_seq_escala_w		bigint;
nr_seq_item_sup_w		bigint;
nr_seq_result_item_w	bigint;
ds_retorno_w		varchar(25) := 'S';

c01 CURSOR FOR
	SELECT	m.nr_seq_item_sup,
		m.nr_seq_result_item
	from	eif_escala_ii_item_regra m
	where	(m.nr_seq_item_inf IS NOT NULL AND m.nr_seq_item_inf::text <> '')
	and	m.nr_seq_escala = nr_seq_escala_w
	and	m.nr_seq_item_inf = nr_seq_item_p;


BEGIN

select	max(coalesce(nr_seq_escala, 0))
into STRICT	nr_seq_escala_w
from	escala_eif_ii
where	nr_sequencia = nr_seq_escala_p;

open c01;
loop
fetch c01 into
	nr_seq_item_sup_w, nr_seq_result_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	count(*)
	into STRICT	qt_reg_escala_ii_w
	from	escala_eif_ii_item m
	where	m.nr_seq_item = nr_seq_item_sup_w
	and	m.nr_seq_result = nr_seq_result_item_w
	and	m.nr_seq_escala = nr_seq_escala_p;

	if (qt_reg_escala_ii_w > 0) then
		ds_retorno_w := 'N';
	end if;
end loop;
close c01;
return ds_retorno_w;
end;
$body$
