SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION desc_horarios_aprazados ( nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_hora_w		char(5);
ds_horarios_w	varchar(4000);

c01 CURSOR FOR
SELECT  to_char(a.dt_horario,'hh24:mi')
from    prescr_mat_hor a
where   a.nr_prescricao   = nr_prescricao_p
and		a.nr_seq_material = nr_seq_material_p
and		coalesce(a.ie_horario_especial,'N') = 'N'
and		(((to_char(a.dt_horario,'mi') <> '00') and (obter_se_valor_contido(to_char(a.dt_horario,'hh24:mi'),replace(ds_horarios_w,' ',',')) = 'N')) or
		 ((to_char(a.dt_horario,'mi') = '00') and (obter_se_valor_contido(to_char(a.dt_horario,'hh24'),replace(ds_horarios_w,' ',',')) = 'N')))
order by a.dt_horario;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	max(ds_horarios)
	into STRICT	ds_horarios_w
	from	prescr_material
	where   nr_prescricao = nr_prescricao_p
	and     nr_sequencia = nr_seq_material_p;

	if (coalesce(ds_horarios_w::text, '') = '') then
		open C01;
		loop
		fetch C01 into
				ds_hora_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
				ds_horarios_w   := ds_horarios_w || ' ' || ds_hora_w;
		end loop;
		close C01;
	end if;
end if;

return ds_horarios_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
