CREATE OR REPLACE FUNCTION des_obter_acresc_horas_custom ( nr_seq_complex_p bigint, qt_hora_p bigint, ie_opcao_p text default null, ds_tipo_customizacao_p text default null) RETURNS bigint AS $body$
DECLARE


pr_horas_regra_w	des_acrescimo_horas_custom.pr_horas_regra%type;
pr_total_horas_w	des_acrescimo_horas_custom.pr_horas_regra%type := 0;
qt_hora_w		bigint := coalesce(qt_hora_p,0);
qt_retorno_w		bigint := 0;

C01 CURSOR FOR
	SELECT	coalesce(max(pr_horas_regra),0)
	from	des_acrescimo_horas_custom
	where	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and fim_dia(dt_fim_vigencia)
	and	qt_hora_w >= coalesce(qt_horas_regra,qt_hora_w)
	and	ds_tipo_customizacao_p = ie_tipo_customizacao;

/*	ie_opcao_p
	T - Quantidade total de horas
	A - Quantidade de horas adicionais
	P - Percentual de horas adicionais		*/
BEGIN

if (nr_seq_complex_p IS NOT NULL AND nr_seq_complex_p::text <> '') and (qt_hora_w = 0) then
	select	coalesce(max(qt_hora_padrao),0)
	into STRICT	qt_hora_w
	from	des_complexidade_custom
	where	nr_sequencia = nr_seq_complex_p;
end if;

if (qt_hora_w > 0) then
	begin
	open C01;
	loop
	fetch C01 into	
		pr_horas_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		pr_total_horas_w := pr_total_horas_w + pr_horas_regra_w;
	end loop;
	close C01;

	if (pr_total_horas_w > 0) then
		if (ie_opcao_p = 'P') then
			qt_retorno_w := pr_total_horas_w;
		else
			qt_retorno_w := round(qt_hora_w * dividir(pr_total_horas_w,100));
		end if;
	end if;
	end;
end if;

if (coalesce(ie_opcao_p,'T') = 'T') then
	qt_retorno_w := round(qt_hora_w + qt_retorno_w);
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
