CREATE OR REPLACE FUNCTION des_obter_dados_int_integracao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (nr_sequencia_p > 0) then
	begin
	if (ie_opcao_p = 'T') then
		select	substr(obter_desc_expressao(cd_exp_titulo, ds_titulo),1,255)
		into STRICT	ds_retorno_w
		from	int_integracao
		where	nr_sequencia = nr_sequencia_p;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
