CREATE OR REPLACE FUNCTION des_obter_dados_proj_aprov ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_aprovacao_w		timestamp;
nm_usuario_aprov_w	varchar(15);


BEGIN
select	max(dt_aprovacao),
	coalesce(max(nm_usuario_aprov),'X')
into STRICT	dt_aprovacao_w,
	nm_usuario_aprov_w
from	proj_projeto
where	nr_sequencia = nr_sequencia_p;

if (nm_usuario_aprov_w = 'X') then
	ds_retorno_w := '';
elsif (coalesce(ie_opcao_p,'X') = 'D') then
	ds_retorno_w := to_char(dt_aprovacao_w,'dd/mm/yyyy');
elsif (coalesce(ie_opcao_p,'X') = 'N') then
	ds_retorno_w := nm_usuario_aprov_w;
end if;

if (ie_opcao_p = 'A') then
	begin
	if (coalesce(nm_usuario_aprov_w,'X') = 'X') then
		ds_retorno_w := 'N';
	elsif (coalesce(nm_usuario_aprov_w,'X') <> 'X') then
		ds_retorno_w := 'S';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
