CREATE OR REPLACE FUNCTION des_obter_pacote_os ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_pacote_w			bigint;


BEGIN

if (ie_opcao_p = 1) then
	begin
	select	max(nr_seq_pacote)
	into STRICT	ds_retorno_w
	from	des_pacote_ordem_serv
	where	nr_seq_ordem_serv	= nr_sequencia_p;
	end;
elsif (ie_opcao_p = 2) then
	begin
	select	max(nr_seq_pacote)
	into STRICT	nr_seq_pacote_w
	from	des_pacote_ordem_serv
	where	nr_seq_ordem_serv	= nr_sequencia_p;

	select	to_char(max(dt_entrega),'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	des_pacote_versao
	where	nr_sequencia	= nr_seq_pacote_w;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION des_obter_pacote_os ( nr_sequencia_p bigint, ie_opcao_p bigint) FROM PUBLIC;

