CREATE OR REPLACE FUNCTION des_obter_se_ativ_lib (nr_sequencia_p bigint, nr_seq_grupo_trab_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) := 'S';
qt_existe_w	bigint;

BEGIN

if (nr_sequencia_p > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	des_ativ_exec_lib
	where	nr_seq_ativ = nr_sequencia_p;

	if (qt_existe_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	des_ativ_exec_lib
		where	nr_seq_ativ	= nr_sequencia_p
		and	coalesce(nr_seq_grupo_trab,coalesce(nr_seq_grupo_trab_p,0)) = coalesce(nr_seq_grupo_trab_p,0)  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
