CREATE OR REPLACE FUNCTION des_obter_se_usuario_substit ( nm_usuario_p text, nr_seq_grupo_p bigint, dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
qt_existe_w		bigint;
dt_parametro_w		timestamp := trunc(dt_parametro_p);

BEGIN

if (nr_seq_grupo_p > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	grupo_des_delegacao
	where	nr_seq_grupo_des = nr_seq_grupo_p;

	if (qt_existe_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	grupo_des_delegacao
		where	nr_seq_grupo_des 	= nr_seq_grupo_p
		and	nm_usuario_delegacao 	= nm_usuario_p
		and	trunc(dt_parametro_w) between coalesce(dt_inicio_vigencia,dt_parametro_w) and coalesce(dt_fim_vigencia,dt_parametro_w)  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
